import React, { Component } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";
import SearchItem from "./SearchItem";
import TodoList from "./TodoList";
import Pagination from "./Pagination";
import config from "./config";

import "./App.css";

export default class TodoApp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      text: "",
      taskInformation: "",
      isEditing: false,
      editIndex: null,
      searchValue: "",
      searchResult: [],
      taskPagination: [],
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.onDelete = this.onDelete.bind(this);
    this.editHandler = this.editHandler.bind(this);
    this.handleEditChange = this.handleEditChange.bind(this);
    this.searchItemHandler = this.searchItemHandler.bind(this);
    this.onChangePageHandler = this.onChangePageHandler.bind(this);
    this.searchInputHandler = this.searchInputHandler.bind(this);
  }

  componentDidMount() {
    // console.log(config);
    axios
      .get(`${config.JSONBinBaseURL}`, {
        headers: {
          "secret-key":
            "$2b$10$1poBdDsBRlVQB22kJoA40u03nijnLmNUqAyEIMsKM/NKPZulM3Udy",
        },
      })
      .then((result) => {
        // console.log(result);
        const todos = result.data;
        this.setState({
          items: todos,
        });
      })
      .catch((e) => {
        console.error(e);
      });
  }

  handleInputChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleEditChange(e) {
    e.preventDefault();

    const _items = this.state.items.slice();

    _items[this.state.editIndex] = {
      id: this.state.items[this.state.editIndex].id,
      text: this.state.text,
      taskInformation: this.state.taskInformation,
    };

    // console.log(_items);
    this.setState({
      items: _items,
      text: "",
      taskInformation: "",
      isEditing: false,
    });

    axios
      .put(`${config.JSONBinBaseURL}`, _items, {
        headers: {
          "secret-key":
            "$2b$10$1poBdDsBRlVQB22kJoA40u03nijnLmNUqAyEIMsKM/NKPZulM3Udy",
          "Content-Type": "application/json",
          versioning: false,
        },
      })
      .then((res) => {
        // console.log(res);
      })
      .catch((e) => {
        console.error(e);
      });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.items === "") {
      return;
    }
    const newItem = {
      text: this.state.text,
      taskInformation: this.state.taskInformation,
      id: Date.now(),
    };
    this.setState((state) => ({
      text: "",
      items: state.items.concat(newItem),
      taskInformation: "",
    }));
    // console.log(this.state.items);

    axios
      .put(`${config.JSONBinBaseURL}`, this.state.items.concat(newItem), {
        headers: {
          "secret-key":
            "$2b$10$1poBdDsBRlVQB22kJoA40u03nijnLmNUqAyEIMsKM/NKPZulM3Udy",
          "Content-Type": "application/json",
          versioning: false,
        },
      })
      .then((res) => {
        // console.log(res);
      })
      .catch((e) => {
        console.error(e);
      });
  }

  onDelete(forRemoveItem) {
    // console.log(forRemoveItem);
    const data = this.state.items.filter(
      (item) => item.id !== forRemoveItem.id
    );

    this.setState({ items: data });

    axios
      .put(`${config.JSONBinBaseURL}`, data, {
        headers: {
          "secret-key":
            "$2b$10$1poBdDsBRlVQB22kJoA40u03nijnLmNUqAyEIMsKM/NKPZulM3Udy",
          "Content-Type": "application/json",
          versioning: false,
        },
      })
      .then((res) => {})
      .catch((e) => {
        console.error(e);
      });
  }

  editHandler(forEditContent, index) {
    // console.log(forEditContent.id);
    this.setState(() => ({
      text: forEditContent.text,
      taskInformation: forEditContent.taskInformation,
      isEditing: true,
      editIndex: index,
    }));
  }

  searchInputHandler(e) {
    e.preventDefault();

    this.setState({ searchValue: e.target.value });
  }

  searchItemHandler(searchValue, e) {
    if (e) {
      e.preventDefault();
    }

    const items = this.state.items.filter(function (item) {
      return (
        item.text.includes(searchValue) ||
        item.taskInformation.includes(searchValue)
      );
    }, this);

    this.setState({ searchResult: items, searchValue });
  }

  onChangePageHandler(pageNumber) {
    this.setState({ taskPagination: pageNumber });
  }

  render() {
    return (
      <Router>
        <div className="container">
          <div className="row">
            <div className="col-md-6 mx-auto">
              <br />
              <h1 className="text-center">ToDo List App</h1>
            </div>
          </div>
          <br />
          <form
            onSubmit={
              this.state.isEditing ? this.handleEditChange : this.handleSubmit
            }
          >
            <div className="form-group">
              <label htmlFor="new-todo">
                <b>Task Name:</b>
              </label>
              <input
                type="text"
                className="form-control"
                id="new-todo"
                placeholder="Add task name..."
                name="text"
                value={this.state.text}
                onChange={this.handleInputChange}
                required
              />
              <br />
              <label htmlFor="todo-information">
                <b>Task Information:</b>
              </label>
              <textarea
                className="form-control"
                id="todo-information"
                placeholder="Write task information..."
                rows="3"
                name="taskInformation"
                value={this.state.taskInformation}
                onChange={this.handleInputChange}
                required
              ></textarea>
            </div>
            <button type="submit" className="btn btn-primary btn-lg btn-block">
              {this.state.isEditing ? "Save Changes" : "Submit"}
              {this.state.items.text}
              {this.state.items.taskInformation}
            </button>
          </form>
          <SearchItem
            searchItemHandler={this.searchItemHandler}
            searchInputHandler={this.searchInputHandler}
          />
          <TodoList
            items={this.state.items}
            onDelete={this.onDelete}
            editHandler={this.editHandler}
            taskPagination={this.state.taskPagination}
          />
          <Pagination
            pageSize={5}
            items={
              this.state.searchResult.length !== 0 &&
              this.state.searchValue.length !== 0
                ? this.state.searchResult
                : this.state.searchResult.length === 0 &&
                  this.state.searchValue.length !== 0
                ? this.state.searchResult
                : this.state.items
            }
            onChangePageHandler={this.onChangePageHandler}
          />
        </div>
      </Router>
    );
  }
}
